package Assignments;

public class Boolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "Red is favourite colour  ";
		String b = "Orange is also my favourite colour";
		
		
		boolean result1 = s.startsWith("Red");
		boolean result2 = s.startsWith("Orange");
		
		System.out.println(s +" Starts with Red ? "+result1);
		System.out.println(b +" Starts with Red ? "+result2);

	}

}
